import React, { Component } from "react";
import { connect } from "react-redux";
import { removeMovieFavorite } from "../../actions";
import { NavLink } from 'react-router-dom';
import style from './Favorites.module.css';

export class ConnectedList extends Component {

  render() { 
    return (
      <div>
        <h2 className={style.text_wc}>Películas Favoritas</h2>
        <ul>
          {this.props.movie && this.props.movie.map((eve) =>(
            <div>

            <div key={eve.imdbID}>
              <div className={style.container}>
                <div className={style.card_container}>
                  <div className={style.header}>
                    <NavLink to={`/movie/${eve.id}`}>
                    <img src={eve.Poster} className={style.imagen}/>
                    </NavLink>
                    <h2>
                    <NavLink to={`/movie/${eve.id}`} className={style.text_white}>{eve.titulo}</NavLink>
                    </h2>
                    <h4 className={style.text_white}>{eve.Genero}</h4>
                    <h4 className={style.text_white}>{eve.Rango}</h4>
                  </div>
                  <div className={style.description}>
                    <p className={style.text_white}>
                      <strong>Descripción</strong>
                      {eve.desc}
                    </p>
                    <p className={style.text_white}>
                      Actores: {eve.Actores}
                    </p>
                    <button onClick={()=> this.props.remove({id:eve.id})}>X</button>

                  </div>
                </div>
              </div>
            </div>

            </div>
          ))}
        </ul>
      </div>
    );
  }
}

function mapStateToProps(state){
return{
  movie: state.movies
}
}

function mapDispatchToProps(dispatch){
return{
  remove: mov => dispatch(removeMovieFavorite(mov))
}
}

export default connect(mapStateToProps, mapDispatchToProps)(ConnectedList);
